import { Component,OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Match } from '../match';
import { MatchService } from '../match.service';

@Component({
  selector: 'app-match-list',
  template: `
   <app-nav-bar></app-nav-bar>
   <div class="container-md">
   <br>
   <br>
   <br>
   <br>
    <div class="row">


    <div class="card col-md4 mx-1 my-3" style="width: 18rem;" *ngFor="let matche of matches$ | async">
    <img class="card-img-top" src="https://cdn.zeebiz.com/sites/default/files/styles/zeebiz_850x478/public/2022/11/22/211986-fii2kpquuaecc2.jpg?itok=duAxkdck" alt="Card image cap">
    <div class="card-body">
    <h5 class="card-title">NZ v ID</h5>
    </div>
    <ul class="list-group list-group-flush">
    <li class="list-group-item">Time: 14:30 </li>
    <li class="list-group-item">Date:2023/01/31</li>
    <li class="list-group-item">Series:T20 </li>
    </ul>
    <div class="card-body text-center">
    <a href="#" class="btn btn-primary" [routerLink]="['ground']">Book Now</a>
    </div>
    </div>



    </div>
    </div>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <br>
    <app-footer></app-footer>
  `,
  styles: [
  ]
})
export class MatchListComponent implements OnInit{
  matches$: Observable<Match[]> = new Observable();

  constructor(private matchService: MatchService) { }

 ngOnInit(): void {
   this.fetchMatches();
 }

 private fetchMatches(): void {
  this.matches$ = this.matchService.getMatches();
}

}
