import { Component } from '@angular/core';

@Component({
  selector: 'app-stadium',
  template: `
    <p>
      stadium works!
    </p>

    <div *ngFor="let row of seats"> 
    <button *ngFor="let seat of row" [ngClass]="{'occupied': seat.occupied}" (click)="selectSeat(seat)">
    {{ seat.number }}
    </button> 
    </div>
  `,
  styles: [
  ]
})
export class StadiumComponent {

}
